import { Component, EventEmitter, Input, OnInit, Output} from '@angular/core';
import { ITask } from 'src/app/models/interfaces/Task.interface';

@Component({
  selector: 'app-task',
  templateUrl: './task.component.html',
  styleUrls: ['./task.component.scss']
})
export class TaskComponent implements OnInit{

  @Input() task: ITask | undefined;
  @Output() delete: EventEmitter<ITask>= new EventEmitter<ITask>();
  constructor(){}

  ngOnInit(): void {

  }

  deleteTask(){
    console.log("Eliminar tarea", this.task?.title);
    this.delete.emit(this.task); //Notificamos al componente superior la tarea a eliminar
  }
}
<!-- TODO: Reformular con directivas estructurales y estilos condicionales -->

<h5>Title: {{task?.title}}</h5>
<h6>Description: {{task?.description}} </h6>
<p>¿Completed?: {{task?.completed ? 'Yes': 'No'}} </p>

<button (click)="deleteTask()">Delete Task</button>
